#!/usr/bin/env python3

dna = ''

dna_upper = dna.upper() #creates all uppercase version of dna

print ('A count:', dna_upper.count('A')) #prints number of As
print ('T count:', dna_upper.count('T')) #prints number of Ts
print ('C count:', dna_upper.count('C')) #prints number of Cs
print ('G count:', dna_upper.count('G')) #prints number of Gs


