#!/usr/bin/env python3

dna = ""

AT_count = dna.count('A') + dna.count('T') #total number of As and Ts

AT_fraction = AT_count / len(dna) #divides ATs by total number of bases

AT_percent = AT_fraction*100 #coverts to percentage
print ('Percent AT:',AT_percent)

GC_count = dna.count('G') + dna.count('C') #total number of Gs and Cs
GC_fraction = GC_count/ len(dna) #divides GCs by total number of bases
GC_percent= GC_fraction *100
print ('Percent GC:', GC_percent)

print ('AT + GC =', AT_percent + GC_percent) 

